mapper.select:
SELECT T.ID, T.BIZNO, T.INSTID, T.APPLYUSERID, (SELECT EMP.EMPNAME FROM JBOS_EMP EMP WHERE EMP.BADGE=T.APPLYUSERID) AS APPLYUSERNAME, T.APPLYDEPID, (SELECT DEP.DEPNAME FROM JBOS_DEP DEP WHERE DEP.ID=T.APPLYDEPID) AS APPLYDEPNAME, T.FEETYPE, T.APPLYTIME, T.TOTALAMT, T.BIZSTATE FROM IM_MATERIAL_OUTSTORE T WHERE T.ISVALID=1 AND T.APPLYUSERID=#{userId}
AND T.BIZNO LIKE '%${bizNoS}%'
AND T.FEETYPE = #{feeTypeS}
AND date_format(T.APPLYTIME,'%Y-%m-%d')= #{applyTimeS}

mapper.update:
UPDATE IM_MATERIAL_OUTSTORE
SET
    INSTID=#{INSTID},
    BIZSTATE=#{BIZSTATE}
WHERE BIZNO=#{BIZNO}

UPDATE IM_MATERIAL_OUTSTORE
SET
    BIZSTATE=#{BIZSTATE}
WHERE BIZNO=#{BIZNO}
