mapper.select:
SELECT T.ID, T.PROCINSTID, T.PROCDEFID, T.BIZNO, T.USERID, T.BUSINESSKEY, T.STARTTIME, T.ENDTIME, T.PROCSTATE FROM JBOS_PROCESS_INST T WHERE T.ISVALID=1
AND T.USERID=#{userId}
ORDER BY T.STARTTIME

mapper.update:
UPDATE JBOS_PROCESS_INST
SET PROCSTATE=#{procState},ENDTIME=#{endTime}
WHERE PROCINSTID=#{procInstId}
