mapper.select:
SELECT * FROM ( SELECT task.ID_ ID, task.PROC_INST_ID_ PROCINSTID, task.PROC_DEF_ID_ PROCDEFID, T1.BIZID, T1.BIZNO, CASE WHEN T1.BIZTYPE='materialBuy' THEN '物品采购' END BIZTYPE, task.ID_ TASKID, task.NAME_ TASKNAME, task.TASK_DEF_KEY_ TASKDEFKEY, task.CREATE_TIME_ STARTTIME, task.ASSIGNEE_ ASSIGNEE, T1.ROUTEURL, '处理中' TASKSTATE FROM camunda.act_ru_task task LEFT JOIN JBOS_PROCESS_INST T1 ON task.PROC_INST_ID_=T1.PROCINSTID WHERE task.ASSIGNEE_=#{userId}
AND T1.BIZNO LIKE '%${bizNoS}%'
AND T1.BIZTYPE=#{bizTypeS}
) T ORDER BY T.STARTTIME

SELECT * FROM ( SELECT hi.ID_ ID, hi.PROC_INST_ID_ PROCINSTID, hi.PROC_DEF_ID_ PROCDEFID, T1.BIZID, T1.BIZNO, CASE WHEN T1.BIZTYPE='materialBuy' THEN '物品采购' END BIZTYPE, hi.TASK_ID_ TASKID, hi.ACT_NAME_ TASKNAME, hi.ACT_ID_ TASKDEFKEY, hi.START_TIME_ STARTTIME, hi.END_TIME_ ENDTIME, hi.ASSIGNEE_ ASSIGNEE, T1.ROUTEURL, '已处理' TASKSTATE FROM camunda.act_hi_actinst hi LEFT JOIN JBOS_PROCESS_INST T1 ON hi.PROC_INST_ID_=T1.PROCINSTID WHERE hi.ASSIGNEE_=#{userId} and hi.ACT_INST_STATE_ IN (1,4)
AND T1.BIZNO LIKE '%${bizNoS}%'
AND T1.BIZTYPE=#{bizTypeS}
) T ORDER BY T.STARTTIME DESC
