mapper.select:
SELECT * FROM ( SELECT t.ID, t.PROCINSTID, t1.PROCDEFID, T1.BIZID, T1.BIZNO, CASE WHEN T1.BIZTYPE='materialBuy' THEN '物品采购' END BIZTYPE, t.TASKID, t.TASKDEFKEY, t.TASKNAME, t.STARTTIME, t.ASSIGNEE, T1.ROUTEURL, '处理中' TASKSTATE FROM JBOS_PROCESS_TASK t LEFT JOIN JBOS_PROCESS_INST T1 ON t.PROCINSTID=T1.PROCINSTID WHERE t.ASSIGNEE=#{userId} and t.TASKSTATE='20'
AND T1.BIZNO LIKE '%${bizNoS}%'
AND T1.BIZTYPE=#{bizTypeS}
) T ORDER BY T.STARTTIME

SELECT * FROM ( SELECT t.ID, t.PROCINSTID, t1.PROCDEFID, T1.BIZID, T1.BIZNO, CASE WHEN T1.BIZTYPE='materialBuy' THEN '物品采购' END BIZTYPE, t.TASKID, t.TASKDEFKEY, t.TASKNAME, t.STARTTIME, t.ENDTIME, t.ASSIGNEE, T1.ROUTEURL, '已处理' TASKSTATE FROM JBOS_PROCESS_TASK t LEFT JOIN JBOS_PROCESS_INST T1 ON t.PROCINSTID=T1.PROCINSTID WHERE t.ASSIGNEE=#{userId} and t.TASKSTATE IN ('90','99')
AND T1.BIZNO LIKE '%${bizNoS}%'
AND T1.BIZTYPE=#{bizTypeS}
) T ORDER BY T.STARTTIME DESC

mapper.update:
UPDATE JBOS_PROCESS_TASK
SET TASKID=#{taskId},OPINION=#{opinion},OPINIONDESC=#{opinionDesc},ENDTIME=#{endTime},TASKSTATE=#{taskState}
WHERE PROCINSTID=#{procInstId} AND TASKDEFKEY=#{taskDefKey} AND ASSIGNEE=#{assignee} AND TASKSTATE='20'


UPDATE JBOS_PROCESS_TASK
SET ENDTIME=#{endTime},TASKSTATE='10'
WHERE PROCINSTID=#{procInstId} AND TASKSTATE='20' AND STARTTIME<#{startTime}

UPDATE JBOS_PROCESS_TASK
SET TASKSTATE=#{taskState},ENDTIME=#{endTime}
WHERE PROCINSTID=#{procInstId} AND TASKDEFKEY=#{taskDefKey} AND ASSIGNEE=#{assignee} AND TASKSTATE='20'
