mapper.select:
SELECT T.ID,T.PARENTID,T.ROLECODE,T.ROLENAME,T.ROLETYPE
FROM JBOS_ROLE T
WHERE T.ISVALID=1

    AND T.PARENTID = #{parentId}


    AND T.ROLENAME = #{roleNameS}

mapper.update:
UPDATE JBOS_ROLE
SET ROLENAME=#{roleName},ROLETYPE=#{roleType}
WHERE ID=#{id}

mapper.select:
SELECT T.ID,T1.USERID,T.BADGE,T.EMPNAME,T.ORGID, (SELECT B.ORGNAME FROM JBOS_ORG B WHERE B.ID=T.ORGID) AS ORGNAME, T.DEPID, (SELECT B.DEPNAME FROM JBOS_DEP B WHERE B.ID=T.DEPID) AS DEPNAME, (SELECT CONCAT(B.DEPNAME,'-',T.EMPNAME) FROM JBOS_DEP B WHERE B.ID=T.DEPID) AS DEPEMPNAME, T.HEADSHIP, (SELECT B.DICTNAME FROM JBOS_DICT_CODE B WHERE B.TYPEID='JBOS_HEADSHIP' AND B.DICTID=T.HEADSHIP) AS HEADSHIPNAME, T.EMPSTATUS, (SELECT B.DICTNAME FROM JBOS_DICT_CODE B WHERE B.TYPEID='JBOS_EMP_STATUS' AND B.DICTID=T.EMPSTATUS) AS EMPSTATUSNAME FROM JBOS_EMP T INNER JOIN JBOS_USER_ROLE T1 ON T.ID=T1.USERID WHERE T.ISVALID=1 AND T1.ROLEID=#{roleId}
AND T.BADGE = #{badgeS}
AND T.EMPNAME LIKE '%${empNameS}%'
ORDER BY T.ORDERNO

SELECT T.ID,T2.ID USERID,T.BADGE,T.EMPNAME,T.ORGID, (SELECT B.ORGNAME FROM JBOS_ORG B WHERE B.ID=T.ORGID) AS ORGNAME, T.DEPID, (SELECT B.DEPNAME FROM JBOS_DEP B WHERE B.ID=T.DEPID) AS DEPNAME, (SELECT CONCAT(B.DEPNAME,'-',T.EMPNAME) FROM JBOS_DEP B WHERE B.ID=T.DEPID) AS DEPEMPNAME, T.HEADSHIP, (SELECT B.DICTNAME FROM JBOS_DICT_CODE B WHERE B.TYPEID='JBOS_HEADSHIP' AND B.DICTID=T.HEADSHIP) AS HEADSHIPNAME, T.EMPSTATUS, (SELECT B.DICTNAME FROM JBOS_DICT_CODE B WHERE B.TYPEID='JBOS_EMP_STATUS' AND B.DICTID=T.EMPSTATUS) AS EMPSTATUSNAME FROM JBOS_EMP T LEFT JOIN JBOS_USER T2 ON T.BADGE=T2.USERNAME WHERE T.ISVALID =1 AND T.ID NOT IN ( SELECT T1.ID FROM JBOS_EMP T1 INNER JOIN JBOS_USER_ROLE T2 ON T1.ID=T2.USERID WHERE T1.ISVALID=1 AND T2.ROLEID=#{roleId} )
AND T.ORGID = #{orgIdS}
AND T.DEPID = #{depIdS}
AND T.BADGE = #{badgeS}
AND T.EMPNAME LIKE '%${empNameS}%'
ORDER BY T.ORDERNO
